import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';  // Import Navigate for conditional redirects
import LogIn from './components/LogIn';
import Register from './components/Register';
import Home from './components/Home';
import HomeRO from './components/HomeRO';
import ViewCom from './components/ViewCom';
import FileACom from './components/FileACom';
import ResolvedComplaints from './components/ResolvedComplaints';
import UnresolvedComplaints from './components/UnresolvedComplaints';
import Completed from './components/Completed';
import About from './components/About';

export default function App() {
  const [token, setToken] = useState(localStorage.getItem("authToken"));  // Try fetching the token from localStorage on initial render

  return (
    <Container maxWidth="sm">
      <Box sx={{ my: 4 }}>
        <BrowserRouter>
          {/* Handle Routing for Authenticated and Unauthenticated Users */}
          <Routes>
            <Route path="/" element={!token ? <LogIn setToken={setToken} /> : <Navigate to="/fileacom" />} />
            <Route path="/register" element={<Register />} />
            <Route path="/home" element={token ? <Home /> : <Navigate to="/" />} />
            <Route path="/viewcom" element={token ? <ViewCom /> : <Navigate to="/" />} />
            <Route path="/fileacom" element={token ? <FileACom token={token} /> : <Navigate to="/" />} />
            <Route path="/homero" element={token ? <HomeRO /> : <Navigate to="/" />} />
            <Route path="/resolvedComplaints" element={token ? <ResolvedComplaints /> : <Navigate to="/" />} />
            <Route path="/unresolvedComplaints" element={token ? <UnresolvedComplaints /> : <Navigate to="/" />} />
            <Route path="/completed" element={token ? <Completed /> : <Navigate to="/" />} />
            <Route path="/about" element={token ? <About /> : <Navigate to="/" />} />
          </Routes>
        </BrowserRouter>
      </Box>
    </Container>
  );
}
